mapper.select:
select

from tbl_salesman

  store_id = #{store_id,jdbcType=BIGINT}

    and name LIKE '%${name}%'


    and work_id LIKE '%${work_id}%'


    and status = #{status,jdbcType=TINYINT}


ORDER BY create_date DESC
limit #{page_start},#{page_end}

